      } catch (error) {
        console.error('There was an error fetching the data!', error);
        setResults([]); // Clear results on error
      }
    };

    fetchResults();
  }, []);

  const calculateMER = (monthlyReturns) => {
    const averageReturn = monthlyReturns.reduce((acc, val) => acc + val, 0) / monthlyReturns.length;
    return (averageReturn * 12).toFixed(2);
  };

  return (
    <div className="search-container1">
      <h2>Funds Return Information</h2>
      <div className="search-results1">
        {results.map((result, index) => (
          <div key={index} className="search-result1">
            <p><strong>Name:</strong> {result.name}</p>
            <p><strong>Position:</strong> {result.position}</p>
            <p><strong>Year:</strong> {result.year}</p>
            <p><strong>Class Identification Number:</strong> {result.classIdentificationNumber}</p>

            <h3>Monthly Total Return Record</h3>
            {result.monthlyReturns && (
              <div>
                {result.monthlyReturns.map((returnData, idx) => (
                  <p key={idx}>{`Month ${idx + 1}: ${returnData}`}</p>
                ))}
                <p><strong>MER (Annual):</strong> {calculateMER(result.monthlyReturns)}%</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Funds;
