// src/components/Search.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Funds.css';

const Funds = ({ type }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  // const compensationData = useState([]);
  // const compensationQuery = useState('');

  useEffect(() => {
    const fetchResults = async () => {
      if (query.length < 3) {
        setResults([]);
        return;
      }

      try {
        const response = await axios.get('/data/Compensation2.json');
        const data = response.data;

        const filteredResults = data.filter(item =>
          item.companyName.toLowerCase().includes(query.toLowerCase())
        );

        setResults(filteredResults);
      } catch (error) {
        console.error('There was an error fetching the data!', error);
        setResults([]); // Clear results on error
      }
    };

    fetchResults();
  }, [query]);

  // const filteredCompensationData = compensationData.filter(item =>
  //   item.nameAndPosition.toLowerCase().includes(compensationQuery.toLowerCase())
  // );

  return (
    <div className="search-container1">
      <h2>Find a {type}</h2>
      <div className="search-input-container1">
        <input
          type="text"
          placeholder={`Search for a ${type}`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <span className="search-icon1">&#x1F50D;</span> {/* Unicode for search icon */}
      </div>
      <div className="search-results1">
        {results.map((result, index) => (
          <div key={index} className="search-result1">
            <p><strong>Rank:</strong> {result.Rank}</p>
            <p><strong>Name:</strong> {result.Name}</p>
            <p><strong>Country:</strong> {result.Country}</p>
            <p><strong>Sales:</strong> {result.Sales}</p>
            <p><strong>Profit:</strong> {result.Profit}</p>
            <p><strong>Assets:</strong> {result.Assets}</p>
            <p><strong>MarketValue:</strong> {result.MarketValue}</p>
            <p><strong>PercentageOfPortfolio:</strong> {result.PercentageOfPortfolio}</p>
            <p><strong>CompensationApprovalStatus:</strong> {result.CompensationApprovalStatus}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Funds;
