import React, { useState } from 'react';
import './Voting.css';

const Voting = () => {
    const [votes, setVotes] = useState({
        billion: '',
        hundredMillion: '',
        losingCompany: ''
    });

    const handleOptionChange = (e) => {
        const { name, value } = e.target;
        setVotes({
            ...votes,
            [name]: value
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        alert(`You voted:\nNo CEO should be paid $1 billion: ${votes.billion}\nShould CEO be paid $100 million: ${votes.hundredMillion}\nShould CEO of money losing company be paid $100 million: ${votes.losingCompany}`);
        // Add your form submission logic here
    };

    return (
        <div className="voting-container">
            <div className="voting-header">Voice Your Opinion</div>
            <p className="voting-paragraph">
                Here is where visitors can voice their opinion and results will be sent to all CEOs.
                When you disapprove of any public company CEO making over $100 million per year or $1 billion per year,
                we create, send, and track an approved vote to those making under $100 million and a disapproval vote to
                all CEOs being paid over $100 million. Over time, our tools and audience will grow and our influence will grow.
            </p>
            <form className="voting-form" onSubmit={handleSubmit}>
                <div className="voting-item">
                    <span className="voting-statement">No CEO should be paid $1 billion</span>
                    <label>
                        <input
                            type="radio"
                            name="billion"
                            value="Approve"
                            checked={votes.billion === "Approve"}
                            onChange={handleOptionChange}
                        />
                        <span>Approve</span>
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="billion"
                            value="Disapprove"
                            checked={votes.billion === "Disapprove"}
                            onChange={handleOptionChange}
                        />
                        <span>Disapprove</span>
                    </label>
                </div>
                <div className="voting-item">
                    <span className="voting-statement">Should CEO be paid $100 million</span>
                    <label>
                        <input
                            type="radio"
                            name="hundredMillion"
                            value="Approve"
                            checked={votes.hundredMillion === "Approve"}
                            onChange={handleOptionChange}
                        />
                        <span>Approve</span>
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="hundredMillion"
                            value="Disapprove"
                            checked={votes.hundredMillion === "Disapprove"}
                            onChange={handleOptionChange}
                        />
                        <span>Disapprove</span>
                    </label>
                </div>
                <div className="voting-item">
                    <span className="voting-statement">Should CEO of money losing company be paid $100 million</span>
                    <label>
                        <input
                            type="radio"
                            name="losingCompany"
                            value="Approve"
                            checked={votes.losingCompany === "Approve"}
                            onChange={handleOptionChange}
                        />
                        <span>Approve</span>
                    </label>
                    <label>
                        <input
                            type="radio"
                            name="losingCompany"
                            value="Disapprove"
                            checked={votes.losingCompany === "Disapprove"}
                            onChange={handleOptionChange}
                        />
                        <span>Disapprove</span>
                    </label>
                </div>
                <button type="submit" className="submit-button">Submit Vote</button>
            </form>
        </div>
    );
};

export default Voting;
