import React, { useState } from 'react';
import axios from 'axios';
import '../components/Search.css'; // Make sure to create a CSS file for styling

const Search = ({ type }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async () => {
    try {
      const response = await axios.post(
        `https://api.sec-api.io?token=0509059184e169ef9c73486644ede1696a4774adb4f64bc444ef015408d7c603&q=${query}`
      );

      // Assuming the response data structure
      const filings = response.data.filings;

      const reducedResults = filings.reduce((acc, filing) => {
        const { companyName, effectivenessDate, ticker, description } = filing;
        acc.push({ companyName, effectivenessDate, ticker, description });
        return acc;
      }, []);

      setResults(reducedResults);
    } catch (error) {
      console.error('There was an error fetching the data!', error);
      setResults([]); // Clear results on error
    }
  };

  return (
    <div className="search-container">
      <h2>Find a {type}</h2>
      <input
        type="text"
        placeholder={`Search for a ${type}`}
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleSearch}>Search</button>
      <div className="search-results">
        {results.map((result, index) => (
          <div key={index} className="search-result">
            <p><strong>Company Name:</strong> {result.companyName}</p>
            <p><strong>Effectiveness Date:</strong> {result.effectivenessDate}</p>
            <p><strong>Ticker:</strong> {result.ticker}</p>
            <p><strong>Description:</strong> {result.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Search;
