import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Funds.css';

const Funds = () => {
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const API_URL = 'https://api.sec-api.io/?token=6b3bf19df1784cb1a59bf55a7834ca93a3e59ec3cdc6d629837d96b711f99a5e'; // Replace with your actual API token
        const requestBody = {
          query: 'formType:"NPORT-P"',
          from: '0',
          size: '200',
          sort: [{ filedAt: { order: 'desc' } }]
        };

        const response = await axios.post(API_URL, requestBody, {
          headers: {
            'Content-Type': 'application/json'
          }
        });

        const data = response.data.filings;

        const filingDetails = await Promise.all(
          data.map(async (item) => {
            try {
              const proxyUrl = `http://localhost:4000/fetch-xml?url=${encodeURIComponent(item.linkToFilingDetails)}`;
              const filingResponse = await axios.get(proxyUrl);

              // Assuming the text file is JSON formatted
              const returnInfo = filingResponse.data.returnInfo;

              if (returnInfo) {
                const monthlyTotReturns = returnInfo.monthlyTotReturns.map(returnData => ({
                  classId: returnData.classId,
                  rtn1: returnData.rtn1,
                  rtn2: returnData.rtn2,
                  rtn3: returnData.rtn3,
                }));

                return {
                  name: item.name || 'N/A',
                  position: item.position || 'N/A',
                  year: item.year || 'N/A',
                  classIdentificationNumber: returnInfo.classIdentificationNumber || 'N/A',
                  monthlyTotReturns,
                };
              }

              return null;
            } catch (error) {
              console.error(`Error fetching filing details for ${item.name}:`, error.message);
              return null;
            }
          })
        );

        const filteredResults = filingDetails.filter(item => item !== null);
        setResults(filteredResults);
      } catch (error) {
        if (error.response) {
          // The request was made and the server responded with a status code outside the range of 2xx
          console.error('Server responded with an error:', error.response.status, error.response.data);
        } else if (error.request) {
          // The request was made but no response was received
          console.error('No response received:', error.request);
        } else {
          // Something happened in setting up the request that triggered an Error
          console.error('Error in setting up the request:', error.message);
        }

        setResults([]); // Clear results on error
      }
    };

    fetchResults();
  }, []);


  const calculateMER = (monthlyReturns) => {
    const averageReturn = monthlyReturns.reduce((acc, val) => acc + val, 0) / monthlyReturns.length;
    return (averageReturn * 12).toFixed(2);
  };

  return (
    <div className="search-container1">
      <h2>Funds Return Information</h2>
      <div className="search-results1">
        {results.map((result, index) => (
          <div key={index} className="search-result1">
            <p><strong>Name:</strong> {result.name}</p>
            <p><strong>Position:</strong> {result.position}</p>
            <p><strong>Year:</strong> {result.year}</p>
            <p><strong>Class Identification Number:</strong> {result.classIdentificationNumber}</p>

            <h3>Monthly Total Return Record</h3>
            {result.monthlyTotReturns && (
              <div>
                {result.monthlyTotReturns.map((returnData, idx) => (
                  <p key={idx}>{`Month ${idx + 1}: ${returnData.rtn1}, ${returnData.rtn2}, ${returnData.rtn3}`}</p>
                ))}
                <p><strong>MER (Annual):</strong> {calculateMER(result.monthlyTotReturns.map(r => r.rtn1))}%</p>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Funds;
