import React, { useState } from 'react';
import './Voting.css';

const Voting = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionChange = (e) => {
    setSelectedOption(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`You voted: ${selectedOption}`);
    // Add your form submission logic here
  };

  return (
    <div className="voting-container">
      <div className="voting-header">Voice Your Opinion</div>
      <p className="voting-paragraph">
        Here is where visitors can voice their opinion and results will be sent to all CEOs.
        When you disapprove of any public company CEO making over $100 million per year or $1 billion per year,
        we create, send, and track an approved vote to those making under $100 million and a disapproval vote to
        all CEOs being paid over $100 million.
        Over time, our tools and audience will grow and our influence will grow.
      </p>
      <form className="voting-form" onSubmit={handleSubmit}>
        <div className="radio-buttons">
          <label>
            <input
              type="radio"
              value="No CEO should be paid $1 billion"
              checked={selectedOption === "No CEO should be paid $1 billion"}
              onChange={handleOptionChange}
            />
            <span>No CEO should be paid $1 billion</span>
          </label>
          <label>
            <input
              type="radio"
              value="Should CEO be paid $100 million"
              checked={selectedOption === "Should CEO be paid $100 million"}
              onChange={handleOptionChange}
            />
            <span>Should CEO be paid $100 million</span>
          </label>
          <label>
            <input
              type="radio"
              value="Should CEO of money losing company be paid $100 million"
              checked={selectedOption === "Should CEO of money losing company be paid $100 million"}
              onChange={handleOptionChange}
            />
            <span>Should CEO of money losing company be paid $100 million</span>
          </label>
        </div>
        <button type="submit" className="submit-button">Submit Vote</button>
      </form>
    </div>
  );
};

export default Voting;
