import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [fundSearch, setFundSearch] = useState('');
  const [companySearch, setCompanySearch] = useState('');

  const handleSignIn = () => {
    // Implement Google Sign-in logic here
    setIsLoggedIn(true);
  };

  const handleFundSearch = (event) => {
    setFundSearch(event.target.value);
  };

  const handleCompanySearch = (event) => {
    setCompanySearch(event.target.value);
  };

  // API call logic (replace with actual implementation)
  const fetchData = async (searchQuery, type) => {
    const url = `https://api.sec-api.io?token=0509059184e169ef9c73486644ede1696a4774adb4f64bc444ef015408d7c603&type=${type}&q=${searchQuery}`;
    const response = await fetch(url);
    const data = await response.json();
    // Handle and display fetched data
    console.log(`Fetched data for ${type}:`, data);
  };

  useEffect(() => {
    if (fundSearch) {
      fetchData(fundSearch, 'fund');
    }
    if (companySearch) {
      fetchData(companySearch, 'company');
    }
  }, [fundSearch, companySearch]);

  return (
    <div className="App">
      <nav>
        <div className="nav-left">
          <h1>My Say on Pay</h1>
        </div>
        <div className="nav-right">
          <a href="#">Get Started</a>
          <button id="sign-in-btn" onClick={handleSignIn}>
            Sign In
          </button>
        </div>
      </nav>
      <section className="hero">
        <h2>Align my assets with my values</h2>
        <p>Prioritize values, to achieve high-performance growth.</p>
        <ul>
          <li>Join thousands of investors to vote down egregious CEO pay that is robbing you of your high-performance investment returns.</li>
          <li>See how much of your profits are disappearing - up to 25% annually.</li>
          <li>Learn to build your own high-performance portfolio.</li>
        </ul>
        <div className="hero-buttons" hidden={!isLoggedIn}>
          <button id="find-fund-btn">Find a Fund</button>
          <button id="find-company-btn">Find a Company</button>
        </div>
      </section>
      <section className="search-section" hidden={!isLoggedIn}>
        <div className="search-fund">
          <h3>Find a Fund</h3>
          <input
            type="text"
            id="fund-search"
            placeholder="Search for a fund"
            value={fundSearch}
            onChange={handleFundSearch}
          />
        </div>
        <div className="search-company">
          <h3>Find a Company</h3>
          <input
            type="text"
            id="company-search"
            placeholder="Search for a company"
            value={companySearch}
            onChange={handleCompanySearch}
          />
        </div>
      </section>
    </div>
  );
}

export default App;
