// src/components/HeroSection.js
import React from 'react';
import '../components/HeroSection.css'

const HeroSection = () => {
  return (
    <div className="hero-section">
      <h2>Align my assets with my values</h2>
      <h2>Prioritize values, to achieve high-performance growth.</h2>
      <button className="get-started-btn">Get Started</button>
      <p>Join thousands of investors to vote down egregious CEO pay that is robbing you of your high-performance investment returns.</p>
      <p>See how much of your profits are disappearing - up to 25% annually</p>
      <p>Learn to build your own high-performance portfolio.</p>
      <br />
      <br />
      <hr />
      <h2 className="about-us-heading">About Us</h2>
      <p>This site is being developed by Michael Cooper, an investor in small cap stocks who got tired of underperforming CEOs enriching themselves without delivering value to all other stakeholders, including employees and investors.</p>
      <p>Through this site, we will build investor tools to enable investors to find ways to profit by understanding which CEOs and other executives are truly aligned with your objectives.</p>
      <p>Double down on inequality, double up on investor returns.</p>
    </div>
  );
};

export default HeroSection;
