import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Search.css';

const Search = ({ type }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [selectedCompany, setSelectedCompany] = useState(null);

  useEffect(() => {
    const fetchResults = async () => {
      if (query.length < 3) {
        setResults([]);
        return;
      }

      try {
        const response = await axios.post(
          `https://api.sec-api.io?token=12a52247ed6936bd4370b68b388a3b36694cacac397ea679b2356e01bc171cc3&q=${query}`
        );

        const filings = response.data.filings;

        const reducedResults = filings.reduce((acc, filing) => {
          const { companyName, effectivenessDate, ticker, description } = filing;
          acc.push({ companyName, effectivenessDate, ticker, description });
          return acc;
        }, []);

        setResults(reducedResults);
      } catch (error) {
        console.error('There was an error fetching the data!', error);
        setResults([]);
      }
    };

    fetchResults();
  }, [query]);

  const fetchCompanyDetails = async (companyName) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/compensation-data?companyName=${companyName}`);
      setSelectedCompany(response.data);
    } catch (error) {
      console.error('Error fetching company details:', error);
      setSelectedCompany(null);
    }
  };

  return (
    <div className="search-container">
      <h2>Find a {type}</h2>
      <div className="search-input-container">
        <input
          type="text"
          placeholder={`Search for a ${type}`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <span className="search-icon">&#x1F50D;</span>
      </div>
      <div className="search-results">
        {results.map((result, index) => (
          <div key={index} className="search-result" onClick={() => fetchCompanyDetails(result.companyName)}>
            <p><strong>Company Name:</strong> {result.companyName}</p>
            <p><strong>Effectiveness Date:</strong> {result.effectivenessDate}</p>
            <p><strong>Ticker:</strong> {result.ticker}</p>
            <p><strong>Description:</strong> {result.description}</p>
          </div>
        ))}
      </div>
      {selectedCompany && (
        <div className="company-details">
          <h3>{selectedCompany.companyName}</h3>
          <p><strong>Total Maximum Compensation Approved:</strong> {selectedCompany.totalMaximumCompensationApproved}</p>
          <p><strong>Maximum Compensation as a Percentage of Net Income:</strong> {selectedCompany.maximumCompensationAsPercentageOfNetIncome}</p>
          <p><strong>Approval Status:</strong> {selectedCompany.approvalStatus ? 'Approved' : 'Not Approved'}</p>
        </div>
      )}
    </div>
  );
};

export default Search;
