import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Search.css';

const Search = ({ type }) => {
  const [query, setQuery] = useState('');
  const [selectedCompany, setSelectedCompany] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCompanyDetails = async () => {
      if (query.length < 3) {
        setSelectedCompany(null);
        setError(null);
        return;
      }

      try {
        const response = await axios.post(`http://localhost:5000/api/compensation-data?companyName=${query}`);
        setSelectedCompany(response.data);
        setError(null);
      } catch (error) {
        console.error('Error fetching company details:', error);
        setSelectedCompany(null);
        setError('Company not found or there was an error fetching the data.');
      }
    };

    fetchCompanyDetails();
  }, [query]);

  return (
    <div className="search-container">
      <h2>Find a {type}</h2>
      <div className="search-input-container">
        <input
          type="text"
          placeholder={`Search for a ${type}`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <span className="search-icon">&#x1F50D;</span>
      </div>
      {error && <p className="error-message">{error}</p>}
      {selectedCompany && (
        <div className="company-details">
          <h3>{selectedCompany.companyName}</h3>
          <p><strong>Total Maximum Compensation Approved:</strong> {selectedCompany.totalMaximumCompensationApproved}</p>
          <p><strong>Maximum Compensation as a Percentage of Net Income:</strong> {selectedCompany.maximumCompensationAsPercentageOfNetIncome}</p>
          <p><strong>Approval Status:</strong> {selectedCompany.approvalStatus ? 'Approved' : 'Not Approved'}</p>
        </div>
      )}
    </div>
  );
};

export default Search;
