// src/components/Search.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Search.css'; // Assuming the CSS file is here

const Search = ({ type }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [filters, setFilters] = useState({
    maxCompensation: '',
    compensationPercentage: '',
    approvalStatus: ''
  });

  useEffect(() => {
    const fetchResults = async () => {
      if (query.length < 3) {
        setResults([]);
        return;
      }

      try {
        const response = await axios.get('/data/Compensation.json');
        const data = response.data;

        const filteredResults = data.filter(item =>
          item.companyName.toLowerCase().includes(query.toLowerCase())
        );

        setResults(filteredResults);
      } catch (error) {
        console.error('There was an error fetching the data!', error);
        setResults([]); // Clear results on error
      }
    };

    fetchResults();
  }, [query]);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
  };

  const filteredResults = results.filter(result => {
    return (
      (!filters.maxCompensation || result.totalMaximumCompensationApproved <= filters.maxCompensation) &&
      (!filters.compensationPercentage || result.maximumCompensationAsPercentageOfNetIncome <= filters.compensationPercentage) &&
      (!filters.approvalStatus || result.approvalStatus.toLowerCase().includes(filters.approvalStatus.toLowerCase()))
    );
  });

  return (
    <div className="search-container">
      <h2>Find a {type}</h2>
      <div className="search-input-container">
        <input
          type="text"
          placeholder={`Search for a ${type}`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <span className="search-icon">&#x1F50D;</span> {/* Unicode for search icon */}
      </div>

      <div className="filter-input-container">
        <input
          type="number"
          placeholder="Max Compensation"
          name="maxCompensation"
          value={filters.maxCompensation}
          onChange={handleFilterChange}
        />
        <input
          type="number"
          placeholder="Compensation % of Net Income"
          name="compensationPercentage"
          value={filters.compensationPercentage}
          onChange={handleFilterChange}
        />
        <input
          type="text"
          placeholder="Approval Status"
          name="approvalStatus"
          value={filters.approvalStatus}
          onChange={handleFilterChange}
        />
      </div>

      <div className="search-results">
        <table>
          <thead>
            <tr>
              <th>Company Name</th>
              <th>Total Maximum Compensation Approved</th>
              <th>Maximum Compensation as a Percentage of Net Income</th>
              <th>Approval Status</th>
            </tr>
          </thead>
          <tbody>
            {filteredResults.map((result, index) => (
              <tr key={index}>
                <td>{result.companyName}</td>
                <td>{result.totalMaximumCompensationApproved}</td>
                <td>{result.maximumCompensationAsPercentageOfNetIncome}</td>
                <td>{result.approvalStatus}</td>
              </tr>
            ))}
          </tbody>
        </table>
        {filteredResults.length === 0 && query.length >= 3 && (
          <p>No results found for "{query}"</p>
        )}
      </div>
    </div>
  );
};

export default Search;
