import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Import axios
import './Funds.css';

const Funds = () => {
  const [calculatedData, setCalculatedData] = useState([]);
  const [year, setYear] = useState('2024');

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const fileName = year === '2024' ? '/data/MutualFunds.json' : '/data/MutualFunds1.json';
        const response = await axios.get(fileName); // Use axios to fetch data
        const data = response.data;

        // Calculate metrics
        const metrics = data.map(item => {
          const monthlyReturns = Object.values(item.monthly_total_returns);
          const totalMonths = monthlyReturns.length;
          const totalReturn = monthlyReturns.reduce((acc, curr) => acc + curr, 0);
          const averageMonthlyReturn = totalReturn / totalMonths;
          const annualReturn = (averageMonthlyReturn * 12).toFixed(2);
          const returnLostToAdvisor = (item.total_return - annualReturn).toFixed(2);
          const percentageLost = ((returnLostToAdvisor / item.total_return) * 100).toFixed(2);

          return {
            company_name: item.company_name,
            averageMonthlyReturn: averageMonthlyReturn.toFixed(2),
            annualReturn,
            totalReturn: item.total_return.toFixed(2),
            returnLostToAdvisor,
            percentageLost
          };
        });

        setCalculatedData(metrics);

      } catch (error) {
        console.error('Error fetching the JSON data:', error);
      }
    };

    fetchResults();
  }, [year]);

  return (
    <div className="funds">
      <h1>Funds Data</h1>
      <div>
        <button onClick={() => setYear('2023')}>2023</button>
        <button onClick={() => setYear('2024')}>2024</button>
      </div>
      {calculatedData.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Company Name</th>
              <th>Average Monthly Return</th>
              <th>Annual Return</th>
              <th>Total Return</th>
              <th>Return Lost to Advisor</th>
              <th>% Return Lost to Advisor</th>
            </tr>
          </thead>
          <tbody>
            {calculatedData.map((item) => (
              <tr key={item.company_name}>
                <td>{item.company_name}</td>
                <td>{item.averageMonthlyReturn}</td>
                <td>{item.annualReturn}</td>
                <td>{item.totalReturn}</td>
                <td>{item.returnLostToAdvisor}</td>
                <td>{item.percentageLost}%</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No data available for the selected year.</p>
      )}
    </div>
  );
};

export default Funds;
