// src/components/Search.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Search.css'; // Assuming the CSS file is here

const Search = ({ type }) => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  useEffect(() => {
    const fetchResults = async () => {
      if (query.length < 3) {
        setResults([]);
        return;
      }

      try {
        const response = await axios.post(
          `https://api.sec-api.io?token=12a52247ed6936bd4370b68b388a3b36694cacac397ea679b2356e01bc171cc3&q=${query}`
        );

        const filings = response.data.filings;

        const reducedResults = filings.reduce((acc, filing) => {
          const { companyName, effectivenessDate, ticker, description, totalCompensation, compPercentageOfNetIncome, approved } = filing;
          acc.push({ 
            companyName, 
            effectivenessDate, 
            ticker, 
            description, 
            totalCompensation, 
            compPercentageOfNetIncome, 
            approved 
          });
          return acc;
        }, []);

        setResults(reducedResults);
      } catch (error) {
        console.error('There was an error fetching the data!', error);
        setResults([]); // Clear results on error
      }
    };

    fetchResults();
  }, [query]);

  return (
    <div className="search-container">
      <h2>Find a {type}</h2>
      <div className="search-input-container">
        <input
          type="text"
          placeholder={`Search for a ${type}`}
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <span className="search-icon">&#x1F50D;</span> {/* Unicode for search icon */}
      </div>
      <div className="search-results">
        {results.map((result, index) => (
          <div key={index} className="search-result">
            <p><strong>Company Name:</strong> {result.companyName}</p>
            <p><strong>Effectiveness Date:</strong> {result.effectivenessDate}</p>
            <p><strong>Ticker:</strong> {result.ticker}</p>
            <p><strong>Description:</strong> {result.description}</p>
            {type === 'Master Voting' && (
              <>
                <p><strong>Total Maximum Compensation Approved:</strong> {result.totalCompensation}</p>
                <p><strong>Maximum Compensation as a Percentage of Net Income:</strong> {result.compPercentageOfNetIncome}</p>
                <p><strong>Approval Status:</strong> {result.approved ? 'Approved' : 'Not Approved'}</p>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Search;
