// src/components/HeroSection.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../components/HeroSection.css';

const API_URL = 'https://api.sec-api.io';
const TOKEN = '12a52247ed6936bd4370b68b388a3b36694cacac397ea679b2356e01bc171cc3';

const HeroSection = () => {
  const [experts, setExperts] = useState([{ title: '', salary: '', subject: '' }]);
  const [compensationData, setCompensationData] = useState([]);
  const [filings, setFilings] = useState([]);
  const [reducedFilings, setReducedFilings] = useState([]);
  const [loading, setLoading] = useState(true);

  const addRow = () => {
    setExperts([...experts, { title: '', salary: '', subject: '' }]);
  };

  const handleInputChange = (index, event) => {
    const { name, value } = event.target;
    const newExperts = [...experts];
    newExperts[index][name] = value;
    setExperts(newExperts);
  };

  const fetchFilings = async () => {
    try {
      const response = await axios.post(API_URL, {
        params: {
          token: TOKEN,
          from: '0',
          size: '50',
          sort: JSON.stringify([{ filedAt: { order: 'desc' } }]),
        },
      });
      const filingsData = response.data.filings || [];
      setFilings(filingsData);
      return filingsData;
    } catch (error) {
      console.error('Error fetching filings:', error);
      setFilings([]);
      return [];
    }
  };

  const fetchCompensationData = async (cik) => {
    try {
      const compensationUrl = `${API_URL}/compensation/${cik}`;
      const response = await axios.get(compensationUrl, { params: { token: TOKEN } });
      return response.data;
    } catch (error) {
      console.error(`Error fetching compensation data for CIK ${cik}:`, error);
      return null;
    }
  };

  const fetchData = async () => {
    setLoading(true);
    const filingsData = await fetchFilings();
    const cikNumbers = filingsData.map((filing) => filing.cik);
    const compensationPromises = cikNumbers.map(fetchCompensationData);
    const compensationResults = await Promise.all(compensationPromises);
    setCompensationData(compensationResults.filter((data) => data !== null));

    // Reduce filings data example: count filings per CIK
    const reducedData = filingsData.reduce((acc, filing) => {
      const { cik } = filing;
      if (!acc[cik]) {
        acc[cik] = { count: 0, filings: [] };
      }
      acc[cik].count += 1;
      acc[cik].filings.push(filing);
      return acc;
    }, {});

    setReducedFilings(Object.values(reducedData));
    setLoading(false);
  };

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <div className="hero-section">
      <h2>Align my assets with my values</h2>
      <h2>Prioritize values, to achieve high-performance growth.</h2>
      <button className="get-started-btn">Get Started</button>
      <p>Join thousands of investors to vote down egregious CEO pay that is robbing you of your high-performance investment returns.</p>
      <p>See how much of your profits are disappearing - up to 25% annually</p>
      <p>Learn to build your own high-performance portfolio.</p>
      <br />
      <br />
      <hr />
      <div className="about-us-heading">
        <h2>About Us</h2>
        <p>
          {/* About Us content */}
        </p>
      </div>
      <br />
      <hr />
      <br />
      <div className='chat-section1'>
        <h2>Chat Section</h2>
      </div>
      <div className="white-container">
        <div className="chat-section">
          <div className="chat-content">
            <div className="chat-window">
              <h3>Ask a Question</h3>
              <div className="question">
                <p>Is Tesla producing at full capacity?</p>
                <textarea placeholder="Your answer..."></textarea>
              </div>
              <div className="question">
                <p>How serious is Tesla's problem with battery disposal?</p>
                <textarea placeholder="Your answer..."></textarea>
              </div>
              <div className="question">
                <p>Does Musk deserve $50 billion pay?</p>
                <textarea placeholder="Your answer..."></textarea>
              </div>
              <button type="submit" className="submit-chat-btn">Submit</button>
            </div>
            <div className="bidding-section">
              <h3>Bidding</h3>
              <div className="bidding-item">
                <p># 100/min call + 1500 bonus patenting</p>
              </div>
              <div className="bidding-item">
                <p># 1.50 / min call + $700 bonus potential</p>
              </div>
              <div className="bidding-item">
                <p># 0.50/min call + $100.00 bonus potential</p>
              </div>
            </div>
          </div>
        </div>
      </div>
      <hr />
      <div className="subject-matter-experts">
        <h2>Subject Matter Experts</h2>
        <div className="white-container">
          <h3>Investors Chat with Subject Matter Experts</h3>
          <form>
            <table>
              <thead>
                <tr>
                  <th>Title/Description</th>
                  <th>Salary Level</th>
                  <th>Subject</th>
                </tr>
              </thead>
              <tbody>
                {experts.map((expert, index) => (
                  <tr key={index}>
                    <td><input type="text" name="title" value={expert.title} onChange={(e) => handleInputChange(index, e)} placeholder="Title/Description" /></td>
                    <td><input type="text" name="salary" value={expert.salary} onChange={(e) => handleInputChange(index, e)} placeholder="Salary Level" /></td>
                    <td><input type="text" name="subject" value={expert.subject} onChange={(e) => handleInputChange(index, e)} placeholder="Subject" /></td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="form-buttons">
              <button type="button" className="add-row-btn" onClick={addRow}>Add More</button>
              <button type="submit" className="submit-btn">Submit</button>
            </div>
          </form>
        </div>
      </div>
      <hr />
      <div className="executive-compensation">
        <h2>Executive Compensation Data</h2>
        {loading ? (
          <p className="loading">Loading...</p>
        ) : (
          <div>
            {compensationData.map((data, index) => (
              <div key={index} className="compensation-item">
                <h3>Company Name: {data.companyName}</h3>
                <p>CEO Name: {data.executive?.name}</p>
                <p>Total Compensation: {data.executive?.totalCompensation}</p>
                <p>Position: {data.executive?.position}</p>
                <p>Date of Submission: {data.filedAt}</p>
                <p>
                  Link to Filing Details:{' '}
                  <a href={data.linkToFilingDetails} target="_blank" rel="noopener noreferrer">
                    View Filing
                  </a>
                </p>
              </div>
            ))}
          </div>
        )}
      </div>
      <hr />
      <div className="filings">
        <h2>Recent Filings</h2>
        {loading ? (
          <p className="loading">Loading...</p>
        ) : (
          <div>
            {reducedFilings.length > 0 ? (
              reducedFilings.map((item, index) => (
                <div key={index} className="filing-item">
                  <h3>CIK: {item.cik}</h3>
                  <p>Number of Filings: {item.count}</p>
                  {item.filings.map((filing, idx) => (
                    <div key={idx}>
                      <p>Form Type: {filing.formType}</p>
                      <p>Date Filed: {filing.filedAt}</p>
                      <a href={filing.link} target="_blank" rel="noopener noreferrer">
                        View Filing
                      </a>
                    </div>
                  ))}
                </div>
              ))
            ) : (
              <p>No recent filings available.</p>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default HeroSection;
